package com.robbyp.finances.domain;import lombok.AccessLevel;import lombok.Data;import lombok.NonNull;import lombok.Setter;import org.springframework.data.annotation.Id;import java.math.BigDecimal;import java.time.LocalDate;@Datapublic class Transaction {  @Id  @Setter(AccessLevel.NONE) private String id;  @NonNull @Setter(AccessLevel.NONE) private LocalDate date;  @NonNull @Setter(AccessLevel.NONE) private String description;  @NonNull @Setter(AccessLevel.NONE) private String category;  @NonNull @Setter(AccessLevel.NONE) private Person person;  @NonNull @Setter(AccessLevel.NONE) private BigDecimal amount;}